mapper.select:
select p.pat_id, p.name as pat_name, p.in_hosp_no, p.inp_no, case when p.bed_code is null then '' else p.bed_code end bed_code, p.ward_code as dept_code, p.status, p.barcode, p.gender sex from pat_cure_info p where p.STATUS=1 and p.bed_code is not null
and p.ward_code=#{param1}
and p.pat_id=#{param2}
order by len(p.BED_CODE),p.BED_CODE

select distinct p.pat_id, p.name as pat_name, p.in_hosp_no, case when p.bed_code is null then '' else p.bed_code end bed_code, p.ward_code as dept_code, p.status, p.in_date, p.out_date from pat_cure_info p LEFT JOIN PAT_TRANSFER t on p.PAT_ID= t.PAT_ID where (p.ward_code = #{param4} or t.WARD_CODE=#{param4} or t.NEW_WARD_CODE=#{param4})
and p.pat_id=#{param1}
and p.name=#{param2}
and p.in_hosp_no=#{param3}
order by p.in_date desc

SELECT COUNT(ID)
FROM COM_USER_BED
WHERE USER_CODE =#{param1} AND WARD_CODE =#{param2} AND BED_CODE=#{param3}

mapper.insert:
    	INSERT INTO PAT_DIAGNOSIS(PAT_ID, DIAGNOSIS_INFO, DIAGNOSIS_DATE, IS_IN_DIAG, RECORD_USER, RECORD_DATE, FLAG)
