mapper.select:
    SELECT 

	FROM
		bill_balance B
	WHERE 1=1  


      ORDER BY ${orderByField}

        ${orderBy}



      ORDER BY B.create_time DESC

     LIMIT #{page.startRowNum} ,#{page.pageSize}

SELECT SALER_NO, SALER_NAME, BUYER_NO, BUYER_NAME, SUM(SEND_QTY) OUT_QTY, SUM(SEND_QTY * COST) OUT_AMOUNT, SUM(SEND_QTY) BALANCE_QTY,
IFNULL( (SELECT SUM(DEDUCTION_QTY) FROM OTHER_DEDUCTION OD WHERE 1 = 1
AND OD.BALANCE_NO IS NULL AND OD.BALANCE_TYPE='5' GROUP BY OD.SALER_NO, OD.BUYER_NO), 0 ) DEDUCTION_QTY,
IFNULL((SELECT SUM(DEDUCTION_AMOUNT) FROM OTHER_DEDUCTION OD WHERE 1=1
AND OD.BALANCE_NO IS NULL AND OD.BALANCE_TYPE='5' GROUP BY OD.SALER_NO, OD.BUYER_NO), 0 ) DEDUCTION_AMOUNT,
SUM(SEND_QTY * COST) - IFNULL( (SELECT SUM(DEDUCTION_AMOUNT) FROM OTHER_DEDUCTION OD WHERE 1 = 1
AND OD.BALANCE_NO IS NULL AND OD.BALANCE_TYPE='5' GROUP BY OD.SALER_NO, OD.BUYER_NO), 0 ) BALANCE_AMOUNT FROM BILL_SALE_BALANCE WHERE 1=1 AND BALANCE_NO IS NULL AND BILL_TYPE=1371
AND SALER_NO = #{salerNo}
AND BUYER_NO = #{buyerNo}
AND SEND_DATE >= #{balanceStartDate}
AND SEND_DATE <= #{balanceEndDate}
AND BRAND_UNIT_NO IN ${brandUnitNo}
GROUP BY SALER_NO,BUYER_NO

mapper.update:
  	UPDATE 
	  BILL_SALE_BALANCE
	SET
	  BALANCE_NO = #{billNo}
	WHERE 1 = 1 
	  AND BILL_TYPE = '1371'
	  AND BALANCE_NO IS NULL   
	  AND SALER_NO = #{salerNo}
	  AND BUYER_NO = #{buyerNo}
	  AND SEND_DATE >= #{balanceStartDate}
	  AND SEND_DATE <= #{balanceEndDate}

	  AND BRAND_Unit_NO IN (${brandUnitNo})

UPDATE
	BILL_BUY_BALANCE
SET
	BALANCE_NO = #{billNo}
WHERE 1 = 1
	AND BILL_TYPE='1372'
	AND SALER_NO = #{salerNo}
	AND BUYER_NO = #{buyerNo}
	AND BALANCE_NO IS NULL
	AND SEND_DATE >= #{balanceStartDate}
	AND SEND_DATE <= #{balanceEndDate}

	AND BRAND_NO in (${brandNo})

mapper.select:
SELECT IFNULL(SUM(receivable_amount), 0) total_sum, IFNULL(SUM(overage_amount), 0) over_amount FROM BILL_BALANCE_CASH_PAYMENT WHERE 1=1
and BALANCE_NO = #{params.balanceNo}
and BILL_NO = #{params.billNo}
