mapper.select:
    SELECT 

	FROM
		bill_balance B
	WHERE 1=1  


      ORDER BY ${orderByField}

        ${orderBy}



      ORDER BY B.create_time DESC 

     LIMIT #{page.startRowNum} ,#{page.pageSize}

SELECT BUYER_NO BUYER_NO, SALER_NO SALER_NO, SUM(SEND_QTY) OUT_QTY, SUM(SEND_QTY * COST) OUT_AMOUNT, SUM(SEND_QTY) BALANCE_QTY, SUM(SEND_QTY * COST) BALANCE_AMOUNT FROM BILL_SALE_BALANCE WHERE 1 = 1 AND ( (BILL_TYPE = 1335 AND BIZ_TYPE = '3') OR (BILL_TYPE = 2005 AND BIZ_TYPE = '35') OR (BILL_TYPE = 1361 AND BIZ_TYPE IN ('7', '25')) OR (BILL_TYPE = 1371 AND IS_SPLIT IS NULL) OR (BILL_TYPE='1301' AND BIZ_TYPE='4') OR BILL_TYPE='1333' ) AND BALANCE_NO IS NULL
AND SALER_NO = #{salerNo}
AND BUYER_NO = #{buyerNo}
AND SEND_DATE >= #{balanceStartDate}
AND SEND_DATE <= #{balanceEndDate}
AND BRAND_UNIT_NO IN ${brandUnitNo}
GROUP BY SALER_NO,BUYER_NO

mapper.update:
  	UPDATE 
	  BILL_SALE_BALANCE
	SET
	  BALANCE_NO = #{billNo}
	WHERE 1 = 1 
	  AND (
	      (BILL_TYPE = 1335 AND BIZ_TYPE = '3') 
		  OR (BILL_TYPE = 2005 AND BIZ_TYPE = '35') 
		  OR (BILL_TYPE = 1361 AND BIZ_TYPE IN ('7', '25')) 
		  OR (BILL_TYPE = 1371 AND IS_SPLIT IS NULL)
		  OR (BILL_TYPE='1301' AND BIZ_TYPE='4')
	  	  OR BILL_TYPE='1333'
	  )
	  AND BALANCE_NO IS NULL   
	  AND SALER_NO = #{salerNo}
	  AND BUYER_NO = #{buyerNo}
	  AND SEND_DATE >= #{balanceStartDate}
	  AND SEND_DATE <= #{balanceEndDate}

	  AND BRAND_Unit_NO IN (${brandUnitNo})
