mapper.select:
SELECT 

  FROM bill_balance WHERE 1=1 


   ORDER BY ${orderByField}

     ${orderBy}


  LIMIT #{page.startRowNum} ,#{page.pageSize}

mapper.update:
UPDATE bill_buy_balance T LEFT JOIN category C1 ON LEFT(T.`category_no`, 2) = C1.`category_no` LEFT JOIN category C2 ON LEFT(T.`category_no`, 4) = C2.`category_no` SET balance_no = #{billNo} WHERE 1 = 1 AND (balance_no IS NULL OR balance_no = '') AND saler_no = #{salerNo} AND buyer_no = #{buyerNo} AND send_date >= #{balanceStartDate} AND send_date <= #{balanceEndDate}
AND EXISTS (SELECT 1 FROM supplier_group_rel sgr WHERE T.supplier_no = sgr.supplier_no AND sgr.group_no IN ${supplierGroupNo})
AND NOT EXISTS (SELECT 1 FROM item INNER JOIN brand ON brand.brand_no = item.brand_no INNER JOIN brand_unit ON brand_unit.brand_unit_no = brand.sys_no INNER JOIN supplier_group_rel ON supplier_group_rel.supplier_no = item.supplier_no WHERE 1 = 1 ${priceRangeCondition} AND item.item_no = T.item_no AND ( NOT EXISTS (SELECT 1 FROM official_item OI WHERE OI.item_code = item.code AND OI.supplier_no = item.supplier_no AND OI.brand_no = item.brand_no AND OI.year_no = item.years AND OI.season_no = item.purchase_season) OR EXISTS (SELECT 1 FROM official_item OI WHERE OI.item_code = item.code AND OI.supplier_no = item.supplier_no AND OI.brand_no = item.brand_no AND OI.year_no = item.years AND OI.season_no = item.purchase_season AND OI.excess_status = '1') ) )

		UPDATE bill_sale_balance T LEFT JOIN category C1 
			ON LEFT(T.`category_no`, 2) = C1.`category_no` 
			LEFT JOIN category C2 
			ON LEFT(T.`category_no`, 4) = C2.`category_no`	
	   	 SET balance_no = #{billNo}
    	WHERE 1 = 1 
       		AND (balance_no IS NULL OR balance_no = '')
   			AND saler_no = #{salerNo}
   			AND buyer_no = #{buyerNo}
   			AND send_date >= #{balanceStartDate}
   			AND send_date <= #{balanceEndDate} 


	        	AND EXISTS (SELECT 1 FROM supplier_group_rel sgr WHERE T.supplier_no = sgr.supplier_no AND sgr.group_no IN ${supplierGroupNo})


				AND supplier_no IN ${supplierNo}

	   UPDATE 
		  custom_imperfect T LEFT JOIN category C1 
		ON LEFT(T.`category_no`, 2) = C1.`category_no` 
		LEFT JOIN category C2 
		ON LEFT(T.`category_no`, 4) = C2.`category_no` 
		SET
		  balance_no = #{billNo}
		WHERE 1 = 1 
		  AND (balance_no IS NULL OR balance_no = '') 
  		  AND saler_no = #{salerNo}
  		  AND buyer_no = #{buyerNo}
  		  AND return_date >= #{balanceStartDate}
   		  AND return_date <= #{balanceEndDate}

        	AND EXISTS (SELECT 1 FROM supplier_group_rel sgr WHERE T.saler_no = sgr.supplier_no AND sgr.group_no IN ${supplierGroupNo})

	   UPDATE 
		  other_deduction LEFT JOIN brand 
		  ON other_deduction.brand_no = brand.brand_no
		SET
		  balance_no = #{billNo}
		WHERE 1 = 1 
		  AND (balance_no IS NULL OR balance_no = '') 
  		  AND saler_no = #{salerNo}
  		  AND buyer_no = #{buyerNo}
  		  AND deduction_date >= #{balanceStartDate}
   		  AND deduction_date <= #{balanceEndDate}

			AND balance_type = #{balanceType}


  			AND category_no =  #{categoryNo}



				AND brand.sys_no in ${multiBrandUnitNo}



		  			AND brand.sys_no =  #{brandUnitNo}

	   UPDATE 
		  other_deduction LEFT JOIN brand 
		  ON other_deduction.brand_no = brand.brand_no
		SET
		  balance_no = #{billNo}
		WHERE 1 = 1 
		  AND (balance_no IS NULL OR balance_no = '') 
  		  AND saler_no = #{salerNo}
  		  AND buyer_no = #{buyerNo}
  		  AND deduction_date >= #{balanceStartDate}
   		  AND deduction_date <= #{balanceEndDate}

			AND balance_type = #{balanceType}


  			AND category_no =  #{categoryNo}


  			AND other_deduction.brand_no in (${brandNo})


  			AND other_deduction.brand_no in (SELECT brand_no FROM brand WHERE sys_no IN ${brandUnitNo})

UPDATE bill_buy_balance 
SET balance_no = NULL,  balance_status = NULL
WHERE 1 = 1 
   	AND balance_no = #{balanceNo}
   	AND bill_type = #{billType}

