	UPDATE 
	  bill_balance  
	SET
	  status = #{status} 
	  ,extend_status = #{status} 
	WHERE 1 = 1
   		AND bill_no = #{billNo}

	UPDATE 
	  bill_balance 
	SET
	  status = #{status} 
	  ,extend_status = #{status} 

   		,auditor = #{auditor}
   	 	,audit_time = #{auditTime}


   		,auditor = NULL
   	 	,audit_time = NULL

	WHERE 1 = 1
	AND bill_no = #{billNo}

	   UPDATE 
		  other_deduction LEFT JOIN brand 
		  ON other_deduction.brand_no = brand.brand_no
		SET
		  balance_no = #{billNo}
		WHERE 1 = 1 
		  AND (balance_no IS NULL OR balance_no = '') 
  		  AND saler_no = #{salerNo}
  		  AND buyer_no = #{buyerNo}
  		  AND deduction_date >= #{balanceStartDate}
   		  AND deduction_date <= #{balanceEndDate}

			AND balance_type = #{balanceType}


  			AND category_no =  #{categoryNo}


  			AND other_deduction.brand_no in (${brandNo})

	   UPDATE 
		  other_deduction LEFT JOIN brand 
		  ON other_deduction.brand_no = brand.brand_no
		SET
		  balance_no = #{billNo}
		WHERE 1 = 1 and type=0
		  AND (balance_no IS NULL OR balance_no = '') 
  		  AND saler_no = #{salerNo}
  		  AND buyer_no = #{buyerNo}
  		  AND deduction_date >= #{balanceStartDate}
   		  AND deduction_date <= #{balanceEndDate}

			AND balance_type = #{balanceType}


  			AND category_no =  #{categoryNo}


  			AND other_deduction.brand_no in (${brandNo})


  			AND other_deduction.brand_no in (SELECT brand_no FROM brand WHERE sys_no IN ${brandUnitNo})

	   UPDATE 
		  Bill_Purchase_Adjust_Dtl DTL INNER JOIN Bill_Purchase_Adjust T ON T.bill_no = DTL.bill_no
		SET
		  DTL.area_balance_no = #{billNo}
		WHERE 1 = 1 
		  AND (DTL.area_balance_no IS NULL OR DTL.area_balance_no = '') 
		  AND T.status = 1
  		  AND T.saler_no = #{salerNo}
  		  AND T.buyer_no = #{buyerNo}
  		  AND T.purchase_date >= #{balanceStartDate}
   		  AND T.purchase_date <= #{balanceEndDate}

			AND DTL.supplier_no IN ${supplierNo}


  			AND DTL.brand_no IN  (SELECT brand_no FROM brand WHERE brand.`sys_no` IN ${multiBrandUnitNo})


  			AND DTL.brand_no IN  ${multiBrandNo}


	  		AND DTL.brand_no IN  (SELECT brand_no FROM brand WHERE brand.`sys_no` = #{brandUnitNo})


  			AND LEFT(DTL.category_no,2) =  #{categoryNo}
