mapper.select:
SELECT B.id, B.bill_no, B.bill_type, B.original_bill_no, B.ref_bill_no, B.ref_bill_type, B.STATUS, B.buyer_no, B.buyer_name, B.saler_no, B.saler_name, B.send_date, B.receive_date, B.send_store_no, B.send_store_name, B.receive_store_no, B.receive_store_name, B.sku_id, B.sku_no, B.supplier_no, B.item_no, B.item_code, B.item_name, B.brand_no, B.brand_name, B.brand_unit_no, B.brand_unit_name, B.category_no, B.category_name, B.cost, B.bill_cost, B.actual_cost, B.other_deduct_cost, B.headquarter_cost, B.region_cost, B.unit_cost, B.tax_rate, B.exclusive_cost, B.send_qty, B.order_no, B.balance_no, B.is_split, B.supplier_name, B.order_unit_name, B.organ_no, B.organ_name, B.zone_name, B.order_unit_name_from, B.organ_no_from, B.organ_name_from, B.biz_type, B.zone_name_from, B.bill_rebate_amount , B.bill_rebate_discount, B.unfrozen_date, IFNULL(B.tag_price, 0) tag_price, IFNULL(B.receive_qty, 0) receive_qty, IFNULL(B.send_qty, 0) - IFNULL(B.receive_qty,0) difference_qty, IFNULL(B.cost * B.send_qty, 0) send_amount , IFNULL(B.cost * B.receive_qty, 0) receive_amount, IFNULL(B.cost * (IFNULL(B.send_qty, 0) - IFNULL(B.receive_qty,0)), 0) difference_amount, C1.category_no one_level_category_no, C1.name one_level_category_name, C2.category_no two_level_category_no, C2.name two_level_category_name, t2.name gender_name, SG.supplier_group_name, I.main_color, IFNULL(PP.purchase_price,0) purchase_price, IFNULL(PP.material_price,0) material_price, IFNULL(PP.factory_price ,0) factory_price , IFNULL(PP.purchase_price*send_qty,0) purchase_amount FROM bill_sale_balance B INNER JOIN category C1 ON LEFT(B.`category_no`,2) = C1.`category_no` INNER JOIN category C2 ON LEFT(B.`category_no`,4) = C2.`category_no` LEFT JOIN lookup_entry t2 ON B.`gender` = t2.code AND t2.lookup_id = '7' LEFT JOIN (SELECT t0.group_no supplier_group_no, t0.group_name supplier_group_name, t1.supplier_no supplier_no_sg FROM supplier_group t0,supplier_group_rel t1 WHERE t0.group_no = t1.group_no) SG ON B.supplier_no = SG.supplier_no_sg INNER JOIN item I ON B.item_no=I.item_no LEFT JOIN (SELECT item_no, supplier_no, material_price, purchase_price, factory_price FROM (SELECT PP.item_no, pp.supplier_no, pp.material_price, pp.purchase_price, pp.factory_price FROM purchase_price PP WHERE 1 = 1
AND PP.effective_date <= #{params.balanceEndDate}
ORDER BY item_no DESC, supplier_no DESC, STATUS DESC, effective_date DESC, create_time DESC) G GROUP BY item_no, supplier_no) PP ON B.item_no = PP.item_no AND B.supplier_no = PP.supplier_no WHERE 1 = 1
ORDER BY ${orderByField}
${orderBy}
ORDER BY send_date DESC
LIMIT #{page.startRowNum} ,#{page.pageSize}

