	 	FROM bill_sale_balance
	 WHERE 1 = 1  

      ${params.queryCondition}


      AND send_date >= #{params.balanceStartDate}


      AND send_date <= #{params.balanceEndDate}


      AND saler_no = #{params.salerNo}


