SELECT a3.*, a3.customer_no AS shop_no,a3.customer_name AS short_name FROM ( SELECT a2.biz_type,a2.zone_no,a2.zone_name,a2.company_no,a2.company_name,a2.organ_no,a2.organ_name, a2.customer_no,a2.customer_name,
a2.month,a2.balance_start_date,a2.balance_end_date,
a2.brand_unit_no,a2.brand_unit_name, category.category_nos category_no, category.financial_category_name as category_name,
a2.billMonth,a2.invoice_apply_date,a2.saler_name,
IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.lma_period_salesnum,NULL)),0) AS lma_period_salesnum, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.lma_period_realamount,NULL)),0) AS lma_period_realamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.lma_period_salesamount,NULL)),0) AS lma_period_salesamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.lma_period_tagamount,NULL)),0) AS lma_period_tagamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.lma_period_salescost,NULL)),0) AS lma_period_salescost, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.lma_period_salesdeductions,NULL)),0) AS lma_period_salesdeductions, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.lma_period_balanceamount,NULL)),0) AS lma_period_balanceamount,
IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.bi_period_salesnum,NULL)),0) AS bi_period_salesnum, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.bi_period_realamount,NULL)),0) AS bi_period_realamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.bi_period_salesamount,NULL)),0) AS bi_period_salesamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.bi_period_tagamount,NULL)),0) AS bi_period_tagamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.bi_period_salescost,NULL)),0) AS bi_period_salescost, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.bi_period_salesdeductions,NULL)),0) AS bi_period_salesdeductions, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.bi_period_balanceamount,NULL)),0) AS bi_period_balanceamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.bi_period_balanceamount/1.17,NULL)),0) AS bi_period_non_tax_balanceamount,
IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.bi_period_salesnum,NULL)),0) AS qty, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.bi_period_balanceamount,NULL)),0) AS send_amount,
IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.tma_period_salesnum,NULL)),0) AS tma_period_salesnum, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.tma_period_realamount,NULL)),0) AS tma_period_realamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.tma_period_salesamount,NULL)),0) AS tma_period_salesamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.tma_period_tagamount,NULL)),0) AS tma_period_tagamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.tma_period_salescost,NULL)),0) AS tma_period_salescost, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.tma_period_salesdeductions,NULL)),0) AS tma_period_salesdeductions, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.tma_period_balanceamount,NULL)),0) AS tma_period_balanceamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),a2.tma_period_balanceamount/1.17,NULL)),0) AS tma_period_non_tax_balanceamount,
IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_salesnum,0) - IFNULL(a2.lma_period_salesnum,0),NULL)),0) AS tmi_period_salesnum, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_realamount,0) - IFNULL(a2.lma_period_realamount,0),NULL)),0) AS tmi_period_realamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_salesamount,0) - IFNULL(a2.lma_period_salesamount,0),NULL)),0) AS tmi_period_salesamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_tagamount,0) - IFNULL(a2.lma_period_tagamount,0),NULL)),0) AS tmi_period_tagamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_salescost,0) - IFNULL(a2.lma_period_salescost,0),NULL)),0) AS tmi_period_salescost, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_salesdeductions,0) - IFNULL(a2.lma_period_salesdeductions,0),NULL)),0) AS tmi_period_salesdeductions, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_balanceamount,0) - IFNULL(a2.lma_period_balanceamount,0),NULL)),0) AS tmi_period_balanceamount,
IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_salesnum,0) - IFNULL(a2.lma_period_salesnum,0) + IFNULL(a2.tma_period_salesnum,0),NULL)),0) AS tm_salesnum, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_realamount,0) - IFNULL(a2.lma_period_realamount,0) + IFNULL(a2.tma_period_realamount,0),NULL)),0) AS tm_realamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_salesamount,0) - IFNULL(a2.lma_period_salesamount,0) + IFNULL(a2.tma_period_salesamount,0),NULL)),0) AS tm_salesamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_tagamount,0) - IFNULL(a2.lma_period_tagamount,0) + IFNULL(a2.tma_period_tagamount,0),NULL)),0) AS tm_tagamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_salescost,0) - IFNULL(a2.lma_period_salescost,0) + IFNULL(a2.tma_period_salescost,0),NULL)),0) AS tm_salescost, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_salesdeductions,0) - IFNULL(a2.lma_period_salesdeductions,0) + IFNULL(a2.tma_period_salesdeductions,0),NULL)),0) AS tm_salesdeductions, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_balanceamount,0) - IFNULL(a2.lma_period_balanceamount,0) + IFNULL(a2.tma_period_balanceamount,0),NULL)),0) AS tm_balanceamount,
IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),(IFNULL(a2.bi_period_balanceamount,0) - IFNULL(a2.lma_period_balanceamount,0) + IFNULL(a2.tma_period_balanceamount,0))/1.17,NULL)),0) AS non_tax_salesamount, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.bi_period_salescost,0) - IFNULL(a2.lma_period_salescost,0) + IFNULL(a2.tma_period_salescost,0),NULL)),0) AS tax_cost, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),(IFNULL(a2.bi_period_salescost,0) - IFNULL(a2.lma_period_salescost,0) + IFNULL(a2.tma_period_salescost,0))/1.17,NULL)),0) AS no_tax_costs, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.region_cost,0),NULL)),0) AS region_cost, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.headquarter_cost,0),NULL)),0) AS headquarter_cost, IFNULL(SUM(IF(FIND_IN_SET(a2.category_no,category.category_nos),IFNULL(a2.purchase_price,0),NULL)),0) AS purchase_price
FROM ( SELECT a1.biz_type,a1.zone_no,a1.zone_name,a1.company_no,a1.company_name,a1.organ_no,a1.organ_name, a1.customer_no,a1.customer_name,
a1.month,a1.balance_start_date,a1.balance_end_date,
a1.brand_unit_no,a1.brand_unit_name,a1.category_no,
a1.billMonth,a1.invoice_apply_date,a1.saler_name,
SUM(CASE WHEN a1.sale_date < CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01') THEN a1.qty ELSE NULL END) AS lma_period_salesnum, SUM(CASE WHEN a1.sale_date < CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01') THEN a1.amount ELSE NULL END) AS lma_period_realamount, SUM(CASE WHEN a1.sale_date < CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01') THEN a1.amount ELSE NULL END) AS lma_period_salesamount, SUM(CASE WHEN a1.sale_date < CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01') THEN a1.sum_tag_price ELSE NULL END) AS lma_period_tagamount, SUM(CASE WHEN a1.sale_date < CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01') THEN a1.sum_unit_cost ELSE NULL END) AS lma_period_salescost, SUM(CASE WHEN a1.sale_date < CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01') THEN a1.deductions ELSE NULL END) AS lma_period_salesdeductions, SUM(CASE WHEN a1.sale_date < CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01') THEN a1.amount - a1.deductions ELSE NULL END) AS lma_period_balanceamount,
SUM(CASE WHEN a1.sale_date BETWEEN a1.balance_start_date AND a1.balance_end_date THEN a1.qty ELSE NULL END) AS bi_period_salesnum, SUM(CASE WHEN a1.sale_date BETWEEN a1.balance_start_date AND a1.balance_end_date THEN a1.amount ELSE NULL END) AS bi_period_realamount, SUM(CASE WHEN a1.sale_date BETWEEN a1.balance_start_date AND a1.balance_end_date THEN a1.amount ELSE NULL END) AS bi_period_salesamount, SUM(CASE WHEN a1.sale_date BETWEEN a1.balance_start_date AND a1.balance_end_date THEN a1.sum_tag_price ELSE NULL END) AS bi_period_tagamount, SUM(CASE WHEN a1.sale_date BETWEEN a1.balance_start_date AND a1.balance_end_date THEN a1.sum_unit_cost ELSE NULL END) AS bi_period_salescost, SUM(CASE WHEN a1.sale_date BETWEEN a1.balance_start_date AND a1.balance_end_date THEN a1.deductions ELSE NULL END) AS bi_period_salesdeductions, SUM(CASE WHEN a1.sale_date BETWEEN a1.balance_start_date AND a1.balance_end_date THEN a1.amount - a1.deductions ELSE NULL END) AS bi_period_balanceamount,
SUM(CASE WHEN a1.sale_date > a1.balance_end_date AND a1.sale_date <= LAST_DAY(CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01')) THEN a1.qty ELSE NULL END) AS tma_period_salesnum, SUM(CASE WHEN a1.sale_date > a1.balance_end_date AND a1.sale_date <= LAST_DAY(CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01')) THEN a1.amount ELSE NULL END) AS tma_period_realamount, SUM(CASE WHEN a1.sale_date > a1.balance_end_date AND a1.sale_date <= LAST_DAY(CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01')) THEN a1.amount ELSE NULL END) AS tma_period_salesamount, SUM(CASE WHEN a1.sale_date > a1.balance_end_date AND a1.sale_date <= LAST_DAY(CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01')) THEN a1.sum_tag_price ELSE NULL END) AS tma_period_tagamount, SUM(CASE WHEN a1.sale_date > a1.balance_end_date AND a1.sale_date <= LAST_DAY(CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01')) THEN a1.sum_unit_cost ELSE NULL END) AS tma_period_salescost, SUM(CASE WHEN a1.sale_date > a1.balance_end_date AND a1.sale_date <= LAST_DAY(CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01')) THEN a1.deductions ELSE NULL END) AS tma_period_salesdeductions, SUM(CASE WHEN a1.sale_date > a1.balance_end_date AND a1.sale_date <= LAST_DAY(CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01')) THEN a1.amount - a1.deductions ELSE NULL END) AS tma_period_balanceamount,
SUM(CASE WHEN a1.sale_date BETWEEN CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01') AND LAST_DAY(CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01')) THEN a1.sum_region_cost ELSE NULL END) AS region_cost, SUM(CASE WHEN a1.sale_date BETWEEN CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01') AND LAST_DAY(CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01')) THEN a1.sum_headquarter_cost ELSE NULL END) AS headquarter_cost, SUM(CASE WHEN a1.sale_date BETWEEN CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01') AND LAST_DAY(CONCAT(LEFT(a1.month,4),'-',RIGHT(a1.month,2),'-01')) THEN a1.sum_purchase_price ELSE NULL END) AS purchase_price FROM ( SELECT a.*, bbis.`bill_no` AS invoice_apply_no,bb.saler_name,EXTRACT(YEAR_MONTH FROM bbia.`invoice_apply_date`) AS billMonth,bbia.`invoice_apply_date` FROM ( SELECT CASE WHEN bill_type = '1335' AND biz_type IN ('21','22','29','30','43') biz_type IN ('21','22','29','30') AND saler_no NOT IN (${params.leadRoleCompanyNos}) THEN '2' WHEN ((bill_type = '1361' AND biz_type IN ('7','25')) OR (bill_type = '1335' AND biz_type IN ('3')) OR (bill_type = '2005' AND biz_type IN ('35')) OR (bill_type = '1371') OR (bsb.bill_type = 1333)) AND saler_no NOT IN (${params.leadRoleCompanyNos}) AND buyer_no IN (${params.leadRoleCompanyNos}) THEN '5' ELSE '0' END AS biz_type, EXTRACT(YEAR_MONTH FROM send_date) AS `month`, DATE_ADD(send_date,INTERVAL -DAY(send_date)+1 DAY) AS balance_start_date, LAST_DAY(send_date) AS balance_end_date, zone_no_from AS zone_no,zone_name_from AS zone_name, saler_no AS company_no, saler_name AS company_name, organ_no_from AS organ_no,organ_name_from AS organ_name,
IF(bill_type = '1335' AND biz_type IN ('21','22','29','30','43') biz_type IN ('21','22','29','30') AND saler_no NOT IN (${params.leadRoleCompanyNos}), NULL,buyer_no) AS customer_no, IF(bill_type = '1335' AND biz_type IN ('21','22','29','30','43') biz_type IN ('21','22','29','30') AND saler_no NOT IN (${params.leadRoleCompanyNos}), NULL,buyer_name) AS customer_name,
buyer_no AS customer_no,buyer_name AS customer_name,
id,bill_no AS rel_no,send_date AS sale_date,balance_no, item_no,brand_unit_no,brand_unit_name,brand_no,brand_name,LEFT(category_no, 2) AS category_no, send_qty AS qty,send_qty * cost AS amount,tag_price AS tag_price,tag_price * send_qty AS sum_tag_price,other_deduct_cost AS deductions, unit_cost * send_qty AS sum_unit_cost,region_cost * send_qty AS sum_region_cost,headquarter_cost * send_qty AS sum_headquarter_cost,purchase_price * send_qty AS sum_purchase_price FROM bill_sale_balance bsb WHERE (
(bsb.bill_type = '1335' AND bsb.biz_type = '21' AND bsb.saler_no NOT IN (${params.leadRoleCompanyNos}))
