(bsb.bill_type = '1335' AND bsb.biz_type = '29' AND bsb.saler_no NOT IN (${params.leadRoleCompanyNos}))
(bsb.bill_type = '1335' AND bsb.biz_type = '43' AND bsb.saler_no NOT IN (${params.leadRoleCompanyNos}))
