(bsb.bill_type = '1335' AND bsb.biz_type = '3' AND bsb.saler_no NOT IN (${params.leadRoleCompanyNos}) AND bsb.buyer_no IN (${params.leadRoleCompanyNos}))
(bsb.bill_type = '2005' AND bsb.biz_type = '35' AND bsb.saler_no NOT IN (${params.leadRoleCompanyNos}) AND bsb.buyer_no IN (${params.leadRoleCompanyNos}))
(bsb.bill_type = '1371' AND bsb.saler_no NOT IN (${params.leadRoleCompanyNos}) AND bsb.buyer_no IN (${params.leadRoleCompanyNos}))
(bsb.bill_type = 1333 AND bsb.saler_no NOT IN (${params.leadRoleCompanyNos}) AND bsb.buyer_no IN (${params.leadRoleCompanyNos}))
1=2
)
-- AND @bsb.zone_no!zone_no_from -- AND @bsb.company_no!saler_no
${params.queryCondition}
AND bsb.zone_no_from = #{params.zoneNo}
