UNION ALL SELECT '3' AS biz_type,tbd.`balance_month` AS `month`,tbd.`balance_start_date`,tbd.`balance_end_date`, bsb.zone_no_from AS zone_no,bsb.zone_name_from AS zone_name, bsb.saler_no AS company_no,bsb.saler_name AS company_name, bsb.organ_no_from AS organ_no,bsb.organ_name_from AS organ_name, bsb.buyer_no AS customer_no,bsb.buyer_name AS customer_name, bsb.id,bsb.bill_no AS rel_no,bsb.send_date AS sale_date,bsb.balance_no, bsb.item_no,bsb.brand_unit_no,bsb.brand_unit_name,bsb.brand_no,bsb.brand_name,LEFT(bsb.category_no, 2) AS category_no, bsb.send_qty AS qty,bsb.send_qty * bsb.cost AS amount,bsb.tag_price AS tag_price,bsb.tag_price * bsb.send_qty AS sum_tag_price,other_deduct_cost AS deductions, bsb.unit_cost * bsb.send_qty AS sum_unit_cost,bsb.region_cost * bsb.send_qty AS sum_region_cost,bsb.headquarter_cost * bsb.send_qty AS sum_headquarter_cost,bsb.purchase_price * bsb.send_qty AS sum_purchase_price FROM `transfer_balance_date` AS tbd JOIN bill_sale_balance AS bsb ON tbd.`saler_no` = bsb.saler_no and tbd.buyer_no = bsb.buyer_no AND bsb.send_date BETWEEN tbd.`balance_start_date` AND LAST_DAY(tbd.balance_end_date) WHERE
(bsb.bill_type = '1371' AND bsb.saler_no NOT IN (${params.leadRoleCompanyNos}) AND bsb.buyer_no NOT IN (${params.leadRoleCompanyNos}))
1=2
