${params.queryCondition}
AND bsb.zone_no_from = #{params.zoneNo}
AND bsb.saler_no IN
#{companyNos, jdbcType=CHAR}
AND bsb.organ_no_from IN
#{organNos, jdbcType=CHAR}
AND tbd.balance_month >= EXTRACT(YEAR_MONTH FROM #{params.saleStartDate})
AND tbd.balance_month <= EXTRACT(YEAR_MONTH FROM #{params.saleEndDate})
AND bsb.brand_no IN
#{brandNos, jdbcType=CHAR}
AND LEFT(bsb.category_no, 2) IN
#{categoryNos, jdbcType=CHAR}
AND bsb.item_no IN (SELECT item_no FROM item WHERE 1=1 AND ${params.itemSql})
) AS a LEFT JOIN bill_balance AS bb ON a.balance_no = bb.`bill_no` LEFT JOIN `bill_balance_invoice_source` bbis ON bb.`bill_no` = bbis.balance_no LEFT JOIN `bill_balance_invoice_apply` bbia ON bbis.bill_no = bbia.bill_no WHERE 1=1
AND a.balance_no IS NOT NULL
AND a.balance_no IS NULL
AND (bbia.invoice_apply_date IS NOT NULL
AND bbia.invoice_apply_date >= #{params.invoiceStartDate}
AND bbia.invoice_apply_date <= #{params.invoiceEndDate}
)
AND (bbia.invoice_apply_date IS NULL
OR bbia.invoice_apply_date < #{params.invoiceStartDate}
OR bbia.invoice_apply_date > #{params.invoiceEndDate}
)
) AS a1 GROUP BY a1.biz_type,a1.company_no,a1.organ_no,a1.customer_no,a1.month,a1.brand_unit_no,a1.category_no ) AS a2 JOIN ( SELECT GROUP_CONCAT(category_no SEPARATOR ",") category_nos, financial_category_name FROM category WHERE levelid = 1 /*sys_no*/
AND sys_no = #{params.staticSysNo}
AND category_no IN
#{categoryNos, jdbcType=CHAR}
group by financial_category_name ) AS category GROUP BY a2.biz_type,a2.company_no,a2.organ_no,a2.customer_no,a2.month,a2.brand_unit_no,category.financial_category_name
LIMIT #{params.startRowNum} ,#{params.selectCount}
) AS a3

