mapper.select:
SELECT 

 FROM bill_shop_balance_cate_sum WHERE 1=1 


  ORDER BY ${orderByField}

    ${orderBy}


 LIMIT #{page.startRowNum} ,#{page.pageSize}

mapper.update:
    /*sharding_flag*/
	update bill_shop_balance_cate_sum a,bill_shop_balance b  
    set  a.billing_amount=round(ROUND((a.sale_amount+a.prepayment_amount + diff_amount)/b.system_sales_amount,4)*ifNULL(b.mall_billing_amount,'0.000'),4) 
    WHERE a.balance_no = b.balance_no    
    AND a.balance_no = #{params.balanceNo}

    /*sharding_flag*/
	update bill_shop_balance_cate_sum a,bill_shop_balance b  
    set  a.billing_amount=round(ROUND((a.sale_amount+a.prepayment_amount + diff_amount)/b.system_sales_amount,4)*ifNULL(b.mall_billing_amount,'0.000'),4) 
    WHERE a.balance_no = b.balance_no    
    AND a.balance_no = #{params.balanceNo}

        /*sharding_flag*/
        UPDATE bill_shop_balance_cate_sum a
		SET a.billing_amount = round(a.billing_amount,2) 
		WHERE 1=1
		AND a.balance_no = #{params.balanceNo}

        /*sharding_flag*/
        UPDATE bill_shop_balance_brand a
		SET a.mall_billing_amount = round(a.mall_billing_amount,2) 
		WHERE 1=1
		AND a.balance_no = #{params.balanceNo}

mapper.select:
SELECT id, bill_no, balance_no, company_no, company_name, mall_no, mall_name, shop_no, short_name, full_name, month, balance_start_date, balance_end_date, brand_no, brand_name, category_no, category_name, sale_qty, sale_amount, billing_amount FROM bill_shop_balance_cate_sum WHERE 1=1
AND balance_no in
#{balanceNo}
AND balance_no = #{params.balanceNo}
