mapper.select:
SELECT temp.*,IFNULL(b.`the_payment_amount`,'0.000') AS payment_amount, temp.mall_billing_amount - IFNULL(b.`the_payment_amount`,'0.000') - IFNULL(c.`actual_amount`,'0.000') AS differenceAmount, temp.mall_billing_amount - IFNULL(c.`actual_amount`,'0.000') AS returnedAmount FROM (SELECT DISTINCT , i.taxpayer_no, i.invoice_name, le1.`name` retail_type, (SELECT GROUP_CONCAT( DISTINCT d.invoice_no SEPARATOR "," ) FROM bill_balance_invoice_apply a LEFT JOIN bill_common_invoice_register r ON r.bill_no = a.invoice_register_no LEFT JOIN bill_common_register_dtl d ON r.bill_no = d.bill_no WHERE s.invoice_apply_no = a.bill_no) invoice_no FROM bill_shop_balance s LEFT JOIN shop sp ON s.shop_no = sp.shop_no LEFT JOIN lookup_entry le1 ON sp.retail_type = le1.`code` AND le1.lookup_id IN (SELECT lk.id FROM lookup lk WHERE lk.code = 'SHOP_CATEGORY_B') LEFT JOIN invoice_info i ON i.company_no = s.company_no AND ( i.client_no = s.mall_no OR i.client_no = s.bsgroups_no ) AND i.id = (SELECT MAX(id) FROM invoice_info t WHERE t.company_no = s.company_no AND t.status = 1 AND (t.client_no = s.mall_no)) where 1=1
ORDER BY ${orderByField}
${orderBy}
) AS temp LEFT JOIN ( select dt.balance_no, IFNULL(sum(the_payment_amount),'0.000') AS the_payment_amount from `bill_backsection_split_dtl` dt where 1=1
AND balance_no = #{params.balanceNo}
AND shop_no = #{params.shopNo}
AND company_no = #{params.companyNo}
group by dt.balance_no ) b on temp.balance_no=b.balance_no LEFT JOIN ( select shop_no,month,balance_start_date,balance_end_date,cost_deduct_type,cost_pay_type,IFNULL(sum(actual_amount),'0.000') AS actual_amount from bill_shop_balance_deduct WHERE cost_deduct_type='2' AND cost_pay_type='1'
AND company_no = #{params.companyNo}
AND shop_no = #{params.shopNo}
AND month = #{params.month}
AND month >= '${params.startMonth}'
AND month <= '${params.endMonth}'
group by shop_no,month,balance_start_date,balance_end_date) c on c.shop_no= temp.shop_no AND c.MONTH=temp.month AND c.balance_start_date=temp.balance_start_date AND c.balance_end_date=temp.balance_end_date
ORDER BY temp.create_time DESC
LIMIT #{page.startRowNum} ,#{page.pageSize}

SELECT temp.*,ifNULL(SUM(d.`the_payment_amount`),'0.000') AS payment_amount, temp.mall_billing_amount - IFNULL(SUM(d.`the_payment_amount`),'0.000') - IFNULL( SUM(bd.`actual_amount`), '0.000' ) AS differenceAmount, temp.mall_billing_amount -IFNULL( SUM(bd.`actual_amount`), '0.000' ) AS returnedAmount FROM ( SELECT DISTINCT ,i.taxpayer_no,i.invoice_name, le1.`name` retail_type,
(SELECT GROUP_CONCAT( DISTINCT d.invoice_no SEPARATOR ",") FROM bill_balance_invoice_apply a LEFT JOIN bill_common_invoice_register r ON r.bill_no = a.invoice_register_no LEFT JOIN bill_common_register_dtl d ON r.bill_no = d.bill_no WHERE s.invoice_apply_no = a.bill_no ) invoice_no FROM bill_shop_balance s LEFT JOIN shop sp ON s.shop_no = sp.shop_no LEFT JOIN lookup_entry le1 ON sp.retail_type = le1.`code` AND le1.lookup_id IN (SELECT lk.id FROM lookup lk WHERE lk.code = 'SHOP_CATEGORY_B') LEFT JOIN invoice_info i ON i.company_no = s.company_no AND ( i.client_no = s.mall_no OR i.client_no = s.bsgroups_no) AND i.id = (SELECT MAX(id) FROM invoice_info t WHERE t.company_no = s.company_no AND t.status = 1 AND (t.client_no = s.mall_no))
WHERE 1=1
ORDER BY ${orderByField}
${orderBy}
) AS temp LEFT JOIN `bill_backsection_split_dtl` d ON temp.`balance_no`= d.`balance_no` LEFT JOIN bill_shop_balance_deduct bd ON bd.shop_no= temp.shop_no AND bd.MONTH=temp.month AND bd.balance_start_date=temp.balance_start_date AND bd.balance_end_date=temp.balance_end_date AND bd.cost_deduct_type='2' AND bd.cost_pay_type='1' GROUP BY temp.id ORDER BY temp.create_time DESC
LIMIT #{page.startRowNum} ,#{page.pageSize}

