SELECT sbc.balance_no AS balance_no, sbc.company_no AS company_no, sbc.company_name AS company_name, sbc.mall_no AS mall_no, sbc.mall_name AS mall_name, sb.bsgroups_no as bsgroups_no,sb.bsgroups_name as bsgroups_name,sb.zone_no as zone_no,sb.zone_name as zone_name, sb.organ_no as organ_no,sb.organ_name as organ_name, sbc.shop_no AS shop_no, sbc.short_name AS short_name,sbc.full_name, sbc.month AS month, sbc.balance_start_date AS balance_start_date, sbc.balance_end_date AS balance_end_date, sbc.brand_no AS brand_no, sbc.brand_name AS brand_name, SUM(sbc.sale_amount) as system_sales_amount, SUM(sbc.billing_amount) as billing_amount,sb.invoice_apply_date AS invoice_apply_date, SUM(IFNULL(cc.`ticket_deduction_amount`, 0)) AS ticket_deduction_amount from shop_balance_date sbd LEFT JOIN bill_shop_balance sb on sbd.shop_no=sb.shop_no and sbd.balance_start_date = sb.balance_start_date and sbd.balance_end_date = sb.balance_end_date and sbd.month = sb.month JOIN bill_shop_balance_cate_sum sbc on sb.balance_no = sbc.balance_no LEFT JOIN ( SELECT bsbd.`shop_no`,bsbd.`month`, bsbd.`balance_start_date`,bsbd.`balance_end_date`,bsbd.brand_no,SUM( IFNULL(bsbd.`actual_amount`, 0) ) AS ticket_deduction_amount FROM `bill_shop_balance_deduct` AS bsbd WHERE bsbd.`cost_deduct_type` = 2 AND bsbd.`cost_pay_type` = 1 AND bsbd.`process_status` = 2 GROUP BY bsbd.`shop_no`,bsbd.`month`, bsbd.`balance_start_date`,bsbd.`balance_end_date`,bsbd.brand_no ) cc ON sbc.`shop_no` = cc.`shop_no` AND sbc.`month` = cc.`month` AND sbc.`balance_start_date` = cc.`balance_start_date` AND sbc.`balance_end_date` = cc.`balance_end_date` AND sbc.brand_no = cc.brand_no WHERE 1=1
AND sbd.balance_flag='2' group by sbc.balance_no,sbc.brand_no
UNION ALL
SELECT '' AS balance_no, a.company_no AS company_no, a.company_name AS company_name, a.mall_no AS mall_no, a.mall_name AS mall_name, a.bsgroups_no as bsgroups_no,a.bsgroups_name as bsgroups_name,a.zone_no as zone_no,a.zone_name as zone_name, a.organ_no as organ_no,a.organ_name as organ_name, a.shop_no AS shop_no, a.shop_name AS short_name,a.full_name, a.month AS month, a.balance_start_date AS balance_start_date, a.balance_end_date AS balance_end_date, a.brand_unit_no AS brand_no, a.brand_unit_name AS brand_name, SUM(a.amount) as system_sales_amount, '' as billing_amount,null AS invoice_apply_date,'' AS ticket_deduction_amount FROM ((SELECT om.order_no, om.shop_no,om.shop_name,od.brand_unit_no,od.brand_unit_name,om.company_no,om.company_name,sbd.mall_no,sbd.mall_name, sbd.bsgroups_no as bsgroups_no,sbd.bsgroups_name as bsgroups_name,'' as zone_no,'' as zone_name, s.organ_no as organ_no,g1.name as organ_name, sbd.full_name,sbd.month,sbd.balance_start_date,sbd.balance_end_date, CASE od.balance_base WHEN 1 THEN od.tag_price * od.qty WHEN 0 THEN od.amount ELSE od.amount END AS amount, CASE od.balance_base WHEN 1 THEN od.tag_price * od.qty * IFNULL(od.discount,'0.00') / 100 WHEN 0 THEN od.amount * IFNULL(od.discount,'0.00') / 100 ELSE od.amount * IFNULL(od.discount,'0.00') / 100 END AS deductAmount from shop_balance_date sbd LEFT JOIN shop s on sbd.shop_no=s.shop_no LEFT JOIN organ g ON s.biz_city_no = g.organ_no LEFT JOIN organ g1 ON g.parent_no = g1.organ_no LEFT JOIN order_main om on sbd.shop_no=om.shop_no LEFT JOIN order_dtl od ON om.order_no = od.order_no where sbd.balance_flag='1' AND om.out_date >= sbd.balance_start_date and om.out_date <= sbd.balance_end_date AND om.business_type in('0','1','2','5','6') AND om.status in('30','41')
) UNION ALL (SELECT rem.business_no, rem.shop_no,rem.shop_name,red.brand_unit_no,red.brand_unit_name,rem.company_no,rem.company_name,sbd.mall_no,sbd.mall_name, sbd.bsgroups_no as bsgroups_no,sbd.bsgroups_name as bsgroups_name,'' as zone_no,'' as zone_name, s.organ_no as organ_no,g1.name as organ_name, sbd.full_name,sbd.month,sbd.balance_start_date,sbd.balance_end_date, CASE red.balance_base WHEN 1 THEN red.tag_price * red.qty WHEN 0 THEN red.amount ELSE red.amount END AS amount, CASE red.balance_base WHEN 1 THEN red.tag_price * red.qty * IFNULL(red.discount,'0.00') / 100 WHEN 0 THEN red.amount * IFNULL(red.discount,'0.00') / 100 ELSE red.amount * IFNULL(red.discount,'0.00') / 100 END AS deductAmount from shop_balance_date sbd LEFT JOIN shop s on sbd.shop_no=s.shop_no LEFT JOIN organ g ON s.biz_city_no = g.organ_no LEFT JOIN organ g1 ON g.parent_no = g1.organ_no LEFT JOIN return_exchange_main rem on sbd.shop_no=rem.shop_no LEFT JOIN return_exchange_dtl red ON rem.business_no = red.business_no where sbd.balance_flag='1' AND rem.out_date >= sbd.balance_start_date and rem.out_date <= sbd.balance_end_date AND rem.business_type in('0','1','2','5','6') AND rem.status in('30','41')
)) a GROUP BY a.shop_no,brand_unit_no

