mapper.select:
  	SELECT count(1) total
	FROM (
		SELECT 
			s.shop_no,s.full_name shop_name,
			IFNULL((
				SELECT SUM(op.amount) FROM order_main om LEFT JOIN order_payway op ON om.order_no = op.order_no WHERE 1 = 1 AND om.shop_no = s.shop_no AND om.`status` IN (30,41) AND om.out_date < '${params.startOutDate}' AND om.business_type IN (0, 1, 2, 6) AND op.pay_code = '01'
			),0) om_amount,
			IFNULL((
				SELECT SUM(op.amount) FROM return_exchange_main rem LEFT JOIN order_payway op ON rem.business_no = op.order_no WHERE 1 = 1 AND rem.shop_no = s.shop_no AND rem.`status` IN (30,41) AND rem.out_date < '${params.startOutDate}' AND rem.business_type IN (0, 1, 2, 6) AND op.pay_code = '01'
			),0) rem_amount,
			IFNULL((SELECT SUM(amount) amount FROM deposit_cash WHERE 1 = 1
				AND shop_no = s.shop_no AND end_out_date < '${params.startOutDate}' AND `status` = 0
			),0) deposit_amount,
			IFNULL((SELECT start_amount FROM deposit_set WHERE 1 = 1
				AND shop_no = s.shop_no AND init_date <= '${params.startOutDate}' LIMIT 0,1
			),0) start_amount,
			IFNULL((
				SELECT SUM(op.amount) FROM order_main om LEFT JOIN order_payway op ON om.order_no = op.order_no WHERE 1 = 1 AND om.shop_no = s.shop_no AND om.`status` IN (30,41) AND om.out_date >= '${params.startOutDate}' AND om.out_date <= '${params.startOutDate}' AND om.business_type IN (0, 1, 2, 6) AND op.pay_code = '01'
			),0) om_current_amount,
			IFNULL((
				SELECT SUM(op.amount) FROM return_exchange_main rem LEFT JOIN order_payway op ON rem.business_no = op.order_no WHERE 1 = 1 AND rem.shop_no = s.shop_no AND rem.`status` IN (30,41) AND rem.out_date >= '${params.startOutDate}' AND rem.out_date <= '${params.startOutDate}' AND rem.business_type IN (0, 1, 2, 6) AND op.pay_code = '01'
			),0) rem_current_amount,
			IFNULL((SELECT SUM(amount) amount FROM deposit_cash WHERE 1 = 1
				AND shop_no = s.shop_no AND start_out_date >= '${params.startOutDate}' AND end_out_date <= '${params.endOutDate}' AND `status` = 0
			),0) current_deposit_amount,
			IFNULL((SELECT prepare_cash FROM deposit_set WHERE 1=1 AND shop_no = s.shop_no LIMIT 0,1),0) prepare_cash
		FROM shop s WHERE 1=1


		     and s.company_no = #{params.companyNo}


		     and s.shop_no = #{params.shopNo}


	) A

  	SELECT 
  		A.shop_no,A.shop_name,
  		(A.om_amount+A.rem_amount-A.deposit_amount+A.start_amount) start_amount,
  		(A.om_current_amount+A.rem_current_amount) current_amount,A.current_deposit_amount,
		(A.om_amount+A.rem_amount-A.deposit_amount+A.start_amount+A.om_current_amount+A.rem_current_amount-A.current_deposit_amount) end_amount,
		A.prepare_cash
	FROM (
		SELECT 
			s.shop_no,s.full_name shop_name,
			IFNULL((
				SELECT SUM(op.amount) FROM order_main om LEFT JOIN order_payway op ON om.order_no = op.order_no WHERE 1 = 1 AND om.shop_no = s.shop_no AND om.`status` IN (30,41) AND om.out_date < '${params.startOutDate}' AND om.business_type IN (0, 1, 2, 6) AND op.pay_code = '01'
			),0) om_amount,
			IFNULL((
				SELECT SUM(op.amount) FROM return_exchange_main rem LEFT JOIN order_payway op ON rem.business_no = op.order_no WHERE 1 = 1 AND rem.shop_no = s.shop_no AND rem.`status` IN (30,41) AND rem.out_date < '${params.startOutDate}' AND rem.business_type IN (0, 1, 2, 6) AND op.pay_code = '01'
			),0) rem_amount,
			IFNULL((SELECT SUM(amount) amount FROM deposit_cash WHERE 1 = 1
				AND shop_no = s.shop_no AND end_out_date < '${params.startOutDate}' AND `status` = 0
			),0) deposit_amount,
			IFNULL((SELECT start_amount FROM deposit_set WHERE 1 = 1
				AND shop_no = s.shop_no AND init_date <= '${params.startOutDate}' LIMIT 0,1
			),0) start_amount,
			IFNULL((
				SELECT SUM(op.amount) FROM order_main om LEFT JOIN order_payway op ON om.order_no = op.order_no WHERE 1 = 1 AND om.shop_no = s.shop_no AND om.`status` IN (30,41) AND om.out_date >= '${params.startOutDate}' AND om.out_date <= '${params.startOutDate}' AND om.business_type IN (0, 1, 2, 6) AND op.pay_code = '01'
			),0) om_current_amount,
			IFNULL((
				SELECT SUM(op.amount) FROM return_exchange_main rem LEFT JOIN order_payway op ON rem.business_no = op.order_no WHERE 1 = 1 AND rem.shop_no = s.shop_no AND rem.`status` IN (30,41) AND rem.out_date >= '${params.startOutDate}' AND rem.out_date <= '${params.startOutDate}' AND rem.business_type IN (0, 1, 2, 6) AND op.pay_code = '01'
			),0) rem_current_amount,
			IFNULL((
				SELECT SUM(amount) amount FROM deposit_cash WHERE 1 = 1 AND shop_no = s.shop_no AND start_out_date >= '${params.startOutDate}' AND end_out_date <= '${params.endOutDate}' AND `status` = 0
			),0) current_deposit_amount,
			IFNULL((SELECT prepare_cash FROM deposit_set WHERE 1=1 AND shop_no = s.shop_no LIMIT 0,1),0) prepare_cash
		FROM shop s WHERE 1=1


		     and s.company_no = #{params.companyNo}


		     and s.shop_no = #{params.shopNo}


	) A

		,${orderByField}

			${orderBy}


	LIMIT #{page.startRowNum} ,#{page.pageSize}
