mapper.select:
  	SELECT
		account as deposit_account,deposit_date,SUM(amount) as deposit_amount
	FROM
		deposit_cash
	WHERE 1 = 1 
	AND `status` = 0
	AND confirm_flag = 1


        ${params.queryCondition}


		AND shop_no IN (SELECT shop_no FROM shop WHERE company_no = #{params.companyNo,jdbcType=CHAR})


		AND shop_no = #{params.shopNo,jdbcType=CHAR}


		and deposit_date >= '${params.startOutDate} 00:00:00'


		and deposit_date <= '${params.endOutDate} 23:59:59'


	GROUP BY account,deposit_date

      ORDER BY ${orderByField}

        ${orderBy}


     LIMIT #{page.startRowNum} ,#{page.pageSize}

SELECT IFNULL(SUM(A.amount),0) amount FROM ( SELECT om.shop_no,om.shop_name,om.out_date,op.amount FROM order_main om LEFT JOIN order_payway op ON om.order_no = op.order_no WHERE 1=1 AND op.pay_code = '01'
AND om.shop_no = #{params.shopNo,jdbcType=CHAR}
AND om.out_date >= '${params.startOutDate} 00:00:00'
AND om.out_date <= '${params.endOutDate} 23:59:59'
UNION ALL SELECT rem.shop_no,rem.shop_name,rem.out_date,op.amount FROM return_exchange_main rem LEFT JOIN order_payway op ON rem.business_no = op.order_no WHERE 1=1 AND op.pay_code = '01'
AND rem.shop_no = #{params.shopNo,jdbcType=CHAR}
AND rem.out_date >= '${params.startOutDate} 00:00:00'
AND rem.out_date <= '${params.endOutDate} 23:59:59'
) A
