mapper.select:
    SELECT 

     FROM headquarter_price_rule WHERE 1=1 


      ORDER BY ${orderByField}

        ${orderBy}



		ORDER BY create_time DESC

     LIMIT #{page.startRowNum} ,#{page.pageSize}

SELECT hpr.* FROM item it INNER JOIN supplier_group_rel sgr ON it.supplier_no = sgr.supplier_no INNER JOIN settle_category_dtl scd ON it.root_category_no = scd.category_no INNER JOIN brand b ON it.brand_no = b.brand_no INNER JOIN headquarter_price_rule hpr ON FIND_IN_SET( sgr.group_no, hpr.supplier_group_no ) AND FIND_IN_SET( scd.settle_category_no, hpr.category_no ) AND FIND_IN_SET(b.sys_no, hpr.brand_unit_no) LEFT JOIN `settle_new_style_dtl` snsd ON it.`years` = snsd.`year_code` AND ( it.`sell_season` = snsd.`season_no` OR snsd.season_no = 'ALL' ) WHERE 1 = 1
and it.item_no = #{params.itemNo}
and hpr.add_basis = #{params.addBasis}
AND ( ( hpr.style_type = snsd.style_no AND hpr.new_style_flag = 1 ) OR ( hpr.new_style_flag = 0 AND FIND_IN_SET(it.years, hpr.year_code) AND (hpr.season = it.sell_season OR hpr.season ='ALL') ) ) AND ( FIND_IN_SET( LEFT(it.category_no, 4), hpr.two_level_category_no ) OR ( hpr.two_level_category_no = '' OR hpr.two_level_category_no IS NULL ) ) ORDER BY hpr.effective_time DESC
