			AND B.order_unit_no_from=#{params.orderUnitNoFrom}


			AND B.order_unit_no=#{params.orderUnitNo}


			AND B.category_no like '${params.oneLevelCategoryNo}%'

mapper.select:
SELECT
FROM BILL_SALE_BALANCE B LEFT JOIN CATEGORY C1 ON LEFT(B.`CATEGORY_NO`, 2) = C1.`CATEGORY_NO` LEFT JOIN CATEGORY C2 ON LEFT(B.`CATEGORY_NO`, 4) = C2.`CATEGORY_NO` WHERE 1 = 1 AND ( (B.BILL_TYPE = 1335 AND B.BIZ_TYPE = '3') OR (B.BILL_TYPE = 2005 AND B.BIZ_TYPE = '35') OR (B.BILL_TYPE = 1361 AND B.BIZ_TYPE IN ('7', '25')) OR (B.BILL_TYPE = 1371 AND B.IS_SPLIT IS NULL) OR (B.BILL_TYPE='1301' AND B.BIZ_TYPE='4') OR B.BILL_TYPE='1333' )
ORDER BY ${orderByField}
${orderBy}
ORDER BY SEND_DATE DESC
LIMIT #{page.startRowNum} ,#{page.pageSize}

SELECT
FROM BILL_SALE_BALANCE B LEFT JOIN CATEGORY C1 ON LEFT(B.`CATEGORY_NO`, 2) = C1.`CATEGORY_NO` LEFT JOIN CATEGORY C2 ON LEFT(B.`CATEGORY_NO`, 4) = C2.`CATEGORY_NO` WHERE 1 = 1 AND ( (B.BILL_TYPE = 1335 AND B.BIZ_TYPE = '3') OR (B.BILL_TYPE = 2005 AND B.BIZ_TYPE = '35') OR (B.BILL_TYPE = 1361 AND B.BIZ_TYPE IN ('7', '25')) OR (B.BILL_TYPE = 1371 AND B.IS_SPLIT IS NULL) OR (B.BILL_TYPE='1301' AND B.BIZ_TYPE='4') OR B.BILL_TYPE='1333' )
GROUP BY #{params.groupBy}
