SELECT t.id, t.organ_no, t.organ_code, t. NAME, t.parent_no, t. STATUS, t.organ_level, t.order_no, t.path, t.zone_no, t.create_user, t.create_time, t.update_user, t.update_time, t.remark FROM organ t INNER JOIN ( SELECT organ_no FROM shop WHERE 1=1
AND shop_no = #{params.shopNo}
